import { Component } from '@angular/core';
import { ShellFlyingLine } from 'src/app/core/models/shell_flying_line.model';
import { ScheduleShellAPIService } from 'src/app/core/services/schedule-shell-api.service';

@Component({
  selector: 'sc-schedule-build-page',
  templateUrl: './schedule-build-page.component.html',
  styleUrls: ['./schedule-build-page.component.css']
})
export class ScheduleBuildPageComponent {

  currentTab: number = 0;
  MAX_TABS: number = 4;    
  
  shell_lines: ShellFlyingLine[] = [];
  flying_lines: ShellFlyingLine[] | undefined = [];
  cols: any[] = [];

  startDate: Date = new Date();
  endDate: Date | undefined;

  constructor(private scheduleShellAPI: ScheduleShellAPIService) { }

  ngOnInit() {
    this.startDate = new Date(2020, 4, 4);
    this.cols = [
      { field: 'num', header: 'Blk'},
      { field: 'start_date_time', header: 'Takeoff'}
    ];
  }

  prevNext(inc: number) {
    console.log(this.startDate);
    console.log(this.endDate);

    if (this.startDate == undefined || this.endDate == undefined) {
      console.log('Please select a date');
      return;
    }

    if (this.onStartPage() && inc === 1)
    {
      this.loadFlyingShell();
    }

    this.currentTab += inc;

    if (this.currentTab < 0) {
      this.currentTab = 0;
    }

    if (this.currentTab >= this.MAX_TABS) {
      this.currentTab = this.MAX_TABS - 1;
    }
  }

  onStartPage(): boolean {
    return this.currentTab === 0
  }

  loadFlyingShell(): void {
    const d = new Date('2022-12-05')
    this.scheduleShellAPI.getFlyingShell(d).subscribe(shell => {
      console.log(shell);
      this.shell_lines = shell;
      this.flying_lines = shell;
      console.log(this.shell_lines);
      console.log(this.flying_lines);
    });
  }
}
